import { createContext, useState } from "react";

export const Context = createContext();

const ContextPro = ({ children }) => {
    const [calc, setCalc] = useStatete({
        sign: "",
        num: 0,
        res: 0
    });

    const proValue = {
        calc, setCalc
    }

    return(
        <Context.Provider value={proValue}>
            {children}
        </Context.Provider>
    )
}

export default ContextPro
